// This file defines the notification functionality for your ecommerce project.

// Import the necessary libraries.
const jquery = require('jquery');
const bootstrap = require('bootstrap');


// Define the notification function.
function notification(message) {
  // Create a new notification element.
  const notificationElement = (document.createElement('div'));
  notificationElement.addClass('alert');
  notificationElement.addClass('alert-info');
  notificationElement.text(message);

  // Append the notification element to the body.
  ('#notifications').append(notificationElement);

  // Hide the notification after 5 seconds.
  notificationElement.delay(5000).hide('blind');
}

// Export the notification function.
module.exports = notification;
